# Collections Update Schedules
$Schedule1 = New-CMSchedule -DayOfMonth 8
	
# Defined Device Collections to be created	
New-CMDeviceCollection -Name $CollName -LimitingCollectionName "Branch Workstations Maint. Window" -RefreshSchedule $Schedule1 -RefreshType Both

# Defined Query Rule for Device Collections
Add-CMDeviceCollectionQueryMembershipRule -CollectionName $CollName -QueryExpression $Query -RuleName $CollName

CD E:
#####################################################################################################
$CollNames = 'Prod Group 09', `
             'Prod Group 10', `
             'Prod Group 11', `
             'Prod Group 12', `
             'Prod Group 13', `
             'Prod Group 14', `
             'Prod Group 15', `
             'Prod Group 16'
ForEach ($CollName in $CollNames)
{
# Collections Update Schedules
$Schedule1 = New-CMSchedule -Start "09/07/2018 12:00 AM" -RecurCount 1 -RecurInterval Days
	
# Defined Device Collections to be created	
New-CMDeviceCollection -Name $CollName -LimitingCollectionName "All Windows Workstation or Professional Systems (DC0)" -RefreshSchedule $Schedule1 -RefreshType Both
}

$ErrorActionPreference = 'silentlycontinue'
$DMs = Get-Content "D:\Powershell\!SCCM_PS_scripts\!Collections\Direct_Memberships.txt"
ForEach ($DM in $DMs)
{
    $CollName = $DM.split(';')[0]
    $Computer = $DM.split(';')[1]
    "$Collname`t-`t$Computer"
    Add-CMDeviceCollectionDirectMembershipRule  -CollectionName "$CollName" -ResourceId $(get-cmdevice -Name "$computer").ResourceID
}






